<script>
export default {
    data() {
        return {
            currentFloor: "ground",
            isTooltipVisible: false,
            tooltipContent: "",
            tooltipDesc: "",
            tooltipStyle: {
                display: "none",
                left: 0,
                top: 0,
            },
        };
    },
    methods: {
        showTooltip(event) {
            this.isTooltipVisible = true;
            // Get the data-title and data-description attributes of the hovered element
            const title = event.target.getAttribute("data-title");
            const desc = event.target.getAttribute("data-description");
            // Set the tooltip content and description
            this.tooltipContent = title;
            this.tooltipDesc = desc;
            // Update the position of the tooltip
            this.tooltipStyle = {
                display: "block",
                left: `${event.clientX + window.scrollX}px`,
                top: `${event.clientY + window.scrollY}px`,
            };
        },
        hideTooltip() {
            this.isTooltipVisible = false;
            this.tooltipContent = "";
            this.tooltipDesc = "";
        },
        showFloor(floor){
            this.currentFloor = floor;
        }
    },
};
</script>

